    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        Usuarios users = new Admin(NombreEdit.getText(),ContraEdit.getText());
        if(NombreEdit.getText().isEmpty() || ContraEdit.getText().isEmpty()){
            JOptionPane.showMessageDialog(this, "Favor llenar toods los espacios!");
        }else if(users.buscar(NombreEdit.getText(), 0) != -1){
            nombreAEditar=NombreEdit.getText(); 
            contraAEditar=ContraEdit.getText();
            EditarUsuario edit = new EditarUsuario();
            edit.setVisible(true);
            edit.setLocationRelativeTo(null);
            this.setVisible(false);
        }else{
            JOptionPane.showMessageDialog(this, "Usuario no encontrado!");
        }
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

